# Initialize PancakeSwap router
router = web3.eth.contract(address=router_address, abi=router_abi)

# List of Telegram channels to monitor
channel_list = ['@FirstLedgerHub']

# Function to extract token address from the message
def extract_token_address(message):
    match = re.search(r'0x[a-fA-F0-9]{40}', message)
    return match.group(0) if match else None

# Function to interact with PancakeSwap to buy token
def buy_token(token_address):
    print(f"Buying token at address: {token_address}")
    try:
        tx = router.functions.swapExactETHForTokens(
            0,  # Min tokens to receive
            [web3.toChecksumAddress("0xa65004Cdd48e2634C681dD05C19B34a5caF60E2c"),  # WBNB
             web3.toChecksumAddress(token_address)],
            wallet_address,
            int(time.time()) + 600
        ).buildTransaction({
            'from': wallet_address,
            'value': web3.toWei(0.01, 'ether'),  # Amount to spend
            'gas': 200000,
            'gasPrice': web3.toWei('5', 'gwei'),
            'nonce': web3.eth.getTransactionCount(wallet_address)
        })
        signed_tx = web3.eth.account.signTransaction(tx, private_key)
        tx_hash = web3.eth.sendRawTransaction(signed_tx.rawTransaction)
        print(f"Buy transaction sent: {web3.toHex(tx_hash)}")
        return web3.toHex(tx_hash)
    except Exception as e:
        print(f"Error buying token: {e}")
        return None

# Function to interact with PancakeSwap to sell token
def sell_token(token_address):
    print(f"Selling token at address: {token_address}")
    try:
        tx = router.functions.swapExactTokensForETH(
            web3.toWei(0.01, 'ether'),  # Amount of tokens to sell
            0,  # Min ETH to receive
            [web3.toChecksumAddress(token_address), 
             web3.toChecksumAddress("0xa65004Cdd48e2634C681dD05C19B34a5caF60E2c")],  # Token -> WBNB
            wallet_address,
            int(time.time()) + 600
        ).buildTransaction({
            'from': wallet_address,
            'gas': 200000,
            'gasPrice': web3.toWei('5', 'gwei'),
            'nonce': web3.eth.getTransactionCount(wallet_address)
        })
        signed_tx = web3.eth.account.signTransaction(tx, private_key)
        tx_hash = web3.eth.sendRawTransaction(signed_tx.rawTransaction)
        print(f"Sell transaction sent: {web3.toHex(tx_hash)}")
        return web3.toHex(tx_hash)
    except Exception as e:
        print(f"Error selling token: {e}")
        return None

# Function to send a message via Telegram Bot API
def send_message(chat_id, text):
    url = f"{bot_base_url}/sendMessage"
    data = {"chat_id": chat_id, "text": text}
    requests.post(url, json=data)

# Handle new message events in the target Telegram channels
@client.on(events.NewMessage(chats=channel_list))
async def handle_new_message(event):
    message = event.raw_text
    print(f"Received message: {message}")
    token_address = extract_token_address(message)
    if token_address:
        print(f"Token detected: {token_address}")
        tx_hash = buy_token(token_address)
        if tx_hash:
            send_message("YOUR_TELEGRAM_CHAT_ID", f"Token purchased successfully: {token_address}\nTransaction Hash: {tx_hash}")
            await asyncio.sleep(300)  # Wait 5 minutes
            tx_hash = sell_token(token_address)
            if tx_hash:
                send_message("YOUR_TELEGRAM_CHAT_ID", f"Token sold successfully: {token_address}\nTransaction Hash: {tx_hash}")

# Main function to start the bot
async def main():
    await client.start()
    print("Telegram trading bot is running...")
    await client.run_until_disconnected()

# Run the bot
if __name__ == "__main__":
    asyncio.run(main())
